import React, {useState} from 'react'
import { TodoItem } from './TodoItem';

export const Todos = (props) => {
  //onClick={()=>onDelete(items.sno)}
  return (
    <>
      { props.todoItems.map((items) => (
        <ul key={items.sno}>
          <h4> {items.title} </h4>
          <p> {items.desc} </p>
          <button type="button" class="btn btn-danger btn-sm">Delete</button>
        </ul>
      ))
      }
    </>
  )
}
