import React, {useState} from 'react'

export const TodoItem = () => {
  const initialItems = [
    {
      sno: 1,
      title: "Go to Market",
      desc: "Job 1"
    },
    {
      sno: 2,
      title: "Go to Mall",
      desc: "Job 2"
    },
    {
      sno: 3,
      title: "Go to Ghat",
      desc: "Job 3"
    }
  ]

  const [items, setItems] = useState(initialItems);

  function onDelete(sno){
    const newItems = items.filter((item) => item.sno !== sno);
    setItems(newItems);
  }
  return (
    <>
      {items.map((items) => (
        <ul key={items.sno}>
          <h2> {items.title} </h2>
          <p> {items.desc} </p>
          <button type="button" class="btn btn-danger btn-sm" onClick={() => onDelete(items.sno)}>Delete</button>
        </ul>
      ))}
    </>

  )
}
