import React,{useState} from 'react'
import { TodoItem } from './TodoItem';
import Icon from '../pics/Icon.png';

export const Button = () =>{
    const [click, setClick] = useState(false);
    const onClick = () => setClick(true);

    return(
        <>
        <TodoItem />
        {!click? 
        <button onClick={onClick}><img src={Icon} alt="add item" width="30" /> </button>
        :
        <TodoItem />
        }
        </>
    )
}
