import React from 'react'
import {TodoItem} from "./TodoItem";

export default function AddTodo() {
  const value = {title:"", desc:""};

  function onChange(event){
  }

  const submit = (e) =>{
    e.preventDefault();
    <TodoItem value={value} />
  }
  return (
    <div className="container">
    <h3> Add a Todo </h3>
      <form onsubmit={submit}>
        <div class="mb-3">
          <label for="title">Todo title</label>
          <input type="text" class="form-control" value="" id="title" placeholder="Add a Todo"/>
       </div>
        <div class="mb-3">
          <label for="descr">Todo Decription</label>
          <input type="text" class="form-control" id="descr" placeholder="Todo description"/>
        </div>
        <button type="submit" class="btn btn-sm btn-success">Submit</button>
      </form>
    </div>
  )
}
