import React, {useState} from 'react'

export const TodoItem = ({additem}, {onDelete}) => {
  //const newItem = {
  //    sno: 8,
  //    title: props.title,
  //    desc: props.desc,
   // }
   // setItems([...items, newItem]);

   const [items, setItems] = useState(initialItems);

   function onDelete(sno){
    const newItems = items.filter((item) => item.sno !== sno);
    setItems(newItems);
  }
  return (
    <>
      { additem.map((items) => (
        <ul key={items.sno}>
          <h4> {items.title} </h4>
          <p> {items.desc} </p>
          <button type="button" class="btn btn-danger btn-sm" onClick={() => onDelete(items.sno)}>Delete</button>
        </ul>
      ))}
    </>

  )
}
