import React,{useState} from 'react'

export const TodoItem = ({items}) => {
  const [click, setClick] = useState(false);
  const onClick = () => setClick(true);

  return (
    <div>
      <h4> {items.title} </h4>
      <p> {items.desc} </p>
      {!click? 
        <button className="btn btn-sm btn-danger" onClick={onClick}>Click Me</button>
        :
        <p> Here I am</p>
      }

    </div>
  )
}
