import React, {useState} from 'react'

export default function AddTodo(addTddo) {
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");

  function onChange(event){
    console.log(event.id)
    if (event.id === "title") setTitle(event.value);
    else setDesc(event.value);
  }

  const submit = (e) =>{
    e.preventDefault();
    AddTodo(title, desc);
  }

  return (
    <div className="container">
    <h3> Add a Todo </h3>
      <form onsubmit={submit}>
        <div class="mb-3">
          <label for="title">Todo title</label>
          <input type="text" class="form-control" value={title} id="title" placeholder="Add a Todo" onChange={(e) => onChange(e.target)} />
       </div>
        <div class="mb-3">
          <label for="descr">Todo Decription</label>
          <input type="text" class="form-control" value={desc} id="descr" placeholder="Todo description" onChange={(e) => onChange(e.target)} />
        </div>
        <button type="submit" class="btn btn-sm btn-success">Submit</button>
      </form>
    </div>
  )
}
