import React, {useState} from 'react'
import {TodoItem} from "./TodoItem";

export default function AddTodo() {
  const value = {title:"", desc:""};

  function onChange(event){
    if(event.id==="title"){
      value.title = event.target.value;
      console.log(value.title);
    }
    else{
      value.desc = event.target.value;
      console.log(value.desc);
    }
  }

  const submit = (e) =>{
    e.preventDefault();
    <TodoItem value={value} />
  }

  return (
    <div className="container">
    <h3> Add a Todo </h3>
      <form onsubmit={submit}>
        <div class="mb-3">
          <label for="title">Todo title</label>
          <input type="text" class="form-control" value={value.title} id="title" placeholder="Add a Todo" onchange={(e) => onChange(e.target)} />
       </div>
        <div class="mb-3">
          <label for="descr">Todo Decription</label>
          <input type="text" class="form-control" value={value.desc} id="descr" placeholder="Todo description" onchange={(e) => onChange(e.target)} />
        </div>
        <button type="submit" class="btn btn-sm btn-success">Submit</button>
      </form>
    </div>
  )
}
