  //    title: props.title,
  //    desc: props.desc,
   // }
   // setItems([...items, newItem]);
  //onClick={() => onDelete(items.sno)}
  return (
    <>
      { items.length === 0? "No items to display": 
        items.map((items) => (
        <ul key={items.sno}>
          <h4> {items.title} </h4>
          <p> {items.desc} </p>
          <button type="button" class="btn btn-danger btn-sm">Delete</button>
        </ul>
      ))}
    </>

  )
}
